                    string sInstanceID = oXiLink.XiLinkNVs.Where(m => m.Name.ToLower() == "iInstanceID".ToLower()).Select(m => m.Value).FirstOrDefault();
                    sInstanceID = oCache.Get_ParamVal(sSessionID, sGUID, "", sInstanceID);
